import React from 'react';
import CategoryForm from '../molecules/CategoryForm';
import ProductForm from '../molecules/ProductForm';
import CategoryList from '../organisms/CategoryList';
import ProductList from '../organisms/ProductList';

const AdminTemplate = ({
    categories,
    products,
    onCreateCategory,
    onCreateProduct,
    onEditProduct,
    productToEdit, // Получаем редактируемый продукт
    onEditInitiate // Получаем функцию для инициации редактирования
}) => {
    return (
        <div>
            <h1>Admin Panel</h1>
            <div>
                <h2>Create Category</h2>
                <CategoryForm onSubmit={onCreateCategory} />
                <CategoryList categories={categories} />
            </div>
            <div>
                <h2>Create Product</h2>
                <ProductForm
                    categories={categories}
                    productToEdit={productToEdit} // Передаем редактируемый продукт
                    onSubmit={productToEdit ? (data) => onEditProduct(productToEdit._id, data) : onCreateProduct}
                />
                <ProductList products={products} onEdit={onEditInitiate} />
            </div>
        </div>
    );
};

export default AdminTemplate;
