import React, { useState, useEffect } from 'react';
import { fetchProducts, updateProduct, createProduct } from '../../services/api';  // добавляем функцию updateProduct
import ProductList from '../molecules/ProductList';
import ProductForm from '../molecules/ProductForm';

const AdminPage = () => {
    const [products, setProducts] = useState([]);
    const [categories, setCategories] = useState([]);
    const [editingProduct, setEditingProduct] = useState(null);  // отслеживаем редактируемый продукт

    useEffect(() => {
        async function fetchData() {
            const fetchedProducts = await fetchProducts();
            const fetchedCategories = await fetchCategories();
            setProducts(fetchedProducts);
            setCategories(fetchedCategories);
        }
        fetchData();
    }, []);

    const handleEditProduct = (product) => {
        setEditingProduct(product);  // устанавливаем продукт для редактирования
    };

    const handleFormSubmit = async (productData) => {
        if (editingProduct) {
            // Если редактируем продукт
            await updateProduct(editingProduct._id, productData);  // обновляем продукт
        } else {
            // Если создаем новый продукт
            await createProduct(productData);  // создаем новый продукт
        }
        const updatedProducts = await fetchProducts();  // обновляем список продуктов
        setProducts(updatedProducts);
        setEditingProduct(null);  // сбрасываем состояние редактирования
    };

    return (
        <div>
            <h1>Admin Panel</h1>
            <ProductList products={products} onEdit={handleEditProduct} />
            <ProductForm categories={categories} productToEdit={editingProduct} onSubmit={handleFormSubmit} />
        </div>
    );
};

export default AdminPage;
