import React, { useState } from 'react';
import Input from '../atoms/Input';
import Button from '../atoms/Button';

const ProductForm = ({ categories, onSubmit }) => {
    const [title, setTitle] = useState('');
    const [category, setCategory] = useState('');
    const [type, setType] = useState('');
    const [description, setDescription] = useState('');
    const [cost, setCost] = useState('');
    const [discount, setDiscount] = useState({ percentage: 0, startDate: '', endDate: '' });
    const [colors, setColors] = useState([{ color_name: '', sizes: [{ size_name: '' }], img: [{ img_link: '' }] }]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const productData = {
            title,
            category,
            type,
            description,
            cost,
            discount,
            color: colors
        };
        onSubmit(productData);
    };

    const handleDiscountChange = (field, value) => {
        setDiscount({ ...discount, [field]: value });
    };

    const handleColorChange = (index, field, value) => {
        const newColors = [...colors];
        newColors[index][field] = value;
        setColors(newColors);
    };

    const handleSizeChange = (colorIndex, sizeIndex, value) => {
        const newColors = [...colors];
        newColors[colorIndex].sizes[sizeIndex].size_name = value;
        setColors(newColors);
    };

    const handleImageChange = (colorIndex, imgIndex, value) => {
        const newColors = [...colors];
        newColors[colorIndex].img[imgIndex].img_link = value;
        setColors(newColors);
    };

    const addColor = () => {
        setColors([...colors, { color_name: '', sizes: [{ size_name: '' }], img: [{ img_link: '' }] }]);
    };

    const addSize = (index) => {
        const newColors = [...colors];
        newColors[index].sizes.push({ size_name: '' });
        setColors(newColors);
    };

    const addImage = (index) => {
        const newColors = [...colors];
        newColors[index].img.push({ img_link: '' });
        setColors(newColors);
    };

    return (
        <form onSubmit={handleSubmit}>
            <Input label="Title" value={title} onChange={(e) => setTitle(e.target.value)} />
            <select value={category} onChange={(e) => setCategory(e.target.value)}>
                <option value="">Select Category</option>
                {categories.map((cat) => (
                    <option key={cat._id} value={cat._id}>
                        {cat.title}
                    </option>
                ))}
            </select>
            <Input label="Type" value={type} onChange={(e) => setType(e.target.value)} />
            <Input label="Description" value={description} onChange={(e) => setDescription(e.target.value)} />
            <Input label="Cost" value={cost} onChange={(e) => setCost(e.target.value)} />

            <div>
                <h3>Discount</h3>
                <Input
                    label="Discount Percentage"
                    type="number"
                    value={discount.percentage}
                    onChange={(e) => handleDiscountChange('percentage', e.target.value)}
                />
                <Input
                    label="Discount Start Date"
                    type="date"
                    value={discount.startDate}
                    onChange={(e) => handleDiscountChange('startDate', e.target.value)}
                />
                <Input
                    label="Discount End Date"
                    type="date"
                    value={discount.endDate}
                    onChange={(e) => handleDiscountChange('endDate', e.target.value)}
                />
            </div>

            <div>
                <h3>Colors</h3>
                {colors.map((color, colorIndex) => (
                    <div key={colorIndex}>
                        <Input
                            label="Color Name"
                            value={color.color_name}
                            onChange={(e) => handleColorChange(colorIndex, 'color_name', e.target.value)}
                        />

                        <div>
                            <h4>Sizes</h4>
                            {color.sizes.map((size, sizeIndex) => (
                                <Input
                                    key={sizeIndex}
                                    label="Size"
                                    value={size.size_name}
                                    onChange={(e) => handleSizeChange(colorIndex, sizeIndex, e.target.value)}
                                />
                            ))}
                            <Button label="Add Size" onClick={() => addSize(colorIndex)} />
                        </div>

                        <div>
                            <h4>Images</h4>
                            {color.img.map((image, imgIndex) => (
                                <Input
                                    key={imgIndex}
                                    label="Image Link"
                                    value={image.img_link}
                                    onChange={(e) => handleImageChange(colorIndex, imgIndex, e.target.value)}
                                />
                            ))}
                            <Button label="Add Image" onClick={() => addImage(colorIndex)} />
                        </div>
                    </div>
                ))}
                <Button label="Add Color" onClick={addColor} />
            </div>

            <Button label="Create Product" />
        </form>
    );
};

export default ProductForm;
