import React, { useState, useEffect } from 'react';
import AdminTemplate from '../templates/AdminTemplate';
import { fetchCategories, fetchProducts, createCategory, createProduct } from '../../../services/api';

const AdminPage = () => {
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true); // Добавляем состояние для загрузки
    const [error, setError] = useState(null);     // Добавляем состояние для ошибок

    useEffect(() => {
        const loadData = async () => {
            try {
                const fetchedCategories = await fetchCategories();
                const fetchedProducts = await fetchProducts();
                setCategories(fetchedCategories);
                setProducts(fetchedProducts);
            } catch (err) {
                setError('Failed to load data.');  // Устанавливаем сообщение об ошибке
            } finally {
                setLoading(false);  // Сбрасываем состояние загрузки после завершения
            }
        };
        loadData();
    }, []);

    const handleCreateCategory = async (data) => {
        try {
            const newCategory = await createCategory(data);
            setCategories([...categories, newCategory]);
        } catch (err) {
            setError('Failed to create category.');  // Ошибка при создании категории
        }
    };

    const handleCreateProduct = async (data) => {
        try {
            const newProduct = await createProduct(data);
            setProducts([...products, newProduct]);
        } catch (err) {
            setError('Failed to create product.');  // Ошибка при создании продукта
        }
    };

    if (loading) {
        return <div>Loading...</div>;  // Показываем индикатор загрузки
    }

    if (error) {
        return <div>{error}</div>;  // Показываем сообщение об ошибке
    }

    return (
        <AdminTemplate
            categories={categories}
            products={products}
            onCreateCategory={handleCreateCategory}
            onCreateProduct={handleCreateProduct}
        />
    );
};

export default AdminPage;
